INSERT INTO premios (persona_id, nombre, razon, anio, laureado1, laureado2, laureado3) VALUES

(1, 1, "El Premio Nobel de Física de 1903 fue dividido, una mitad concedida a Antoine Henri Becquerel ""en reconocimiento a los extraordinarios servicios que ha prestado por su descubrimiento de la radioactividad espontánea"", la otra mitad conjuntamente a Pierre Curie y Marie Curie, née Sklodowska ""en reconocimiento de los servicios extraordinarios que han prestado sus investigaciones conjuntas sobre los fenómenos de radiación descubiertos por el profesor Henri Becquerel"".", strftime("1903"), 1, 7, 8),

(1, 2, "El Premio Nobel de Química 1911 fue otorgado a Marie Curie ""en reconocimiento a sus servicios al avance de la química por el descubrimiento de los elementos radio y polonio, por el aislamiento del radio y el estudio de la naturaleza y compuestos de este elemento notable"".", strftime("1911"), 1, NULL, NULL),

(2, 1, "El Premio Nobel de Física de 1903 fue dividido, una mitad concedida a Antoine Henri Becquerel ""en reconocimiento a los extraordinarios servicios que ha prestado por su descubrimiento de la radioactividad espontánea"", la otra mitad conjuntamente a Pierre Curie y Marie Curie, née Sklodowska ""en reconocimiento de los servicios extraordinarios que han prestado sus investigaciones conjuntas sobre los fenómenos de radiación descubiertos por el profesor Henri Becquerel"".", strftime("1903"), 1, 7, 8),

(3, 1, "El Premio Nobel de Física de 1921 fue otorgado a Albert Einstein ""por sus servicios a la Física Teórica, y especialmente por su descubrimiento de la ley del efecto fotoeléctrico"". Albert Einstein recibió su Premio Nobel un año después, en 1922. Durante el proceso de selección en 1921, el Comité Nobel de Física decidió que ninguna de las nominaciones del año cumplía con los criterios establecidos en la voluntad de Alfred Nobel. De acuerdo con los estatutos de la Fundación Nobel, el Premio Nobel puede reservarse en tal caso hasta el año siguiente, y este estatuto se aplicó. Albert Einstein recibió su Premio Nobel de 1921 un año después, en 1922.", strftime("1921"), 2, NULL, NULL),

(4, 5, "El Premio Nobel de la Paz 2014 fue otorgado conjuntamente a Kailash Satyarthi y Malala Yousafzai ""por su lucha contra la represión de niños y jóvenes y por el derecho de todos los niños a la educación"".", strftime("2014"), 3, 9, NULL),

(5, 2, "El Premio Nobel de Química de 1954 fue otorgado a Linus Pauling ""por su investigación sobre la naturaleza del enlace químico y su aplicación a la elucidación de la estructura de sustancias complejas"".", strftime("1954"), 4, NULL, NULL),

(5, 5, "Linus Pauling recibió su Premio Nobel un año después, en 1963. Durante el proceso de selección en 1962, el Comité Noruego del Nobel decidió que ninguna de las nominaciones del año cumplía con los criterios establecidos en la voluntad de Alfred Nobel. De acuerdo con los estatutos de la Fundación Nobel, el Premio Nobel puede reservarse en tal caso hasta el año siguiente, y este estatuto se aplicó. Por lo tanto, Linus Pauling recibió su Premio Nobel por 1962 un año después, en 1963.", strftime("1962"), 4, NULL, NULL),

(6, 4, "El Premio Nobel de Literatura 2007 fue otorgado a Doris Lessing ""la épica de la experiencia femenina, que con escepticismo, fuego y poder visionario ha sometido a una civilización dividida al escrutinio"".", strftime("2007"), 5, NULL, NULL),

(7, 6, "El Premio Sveriges Riksbank en Ciencias Económicas en Memoria de Alfred Nobel 1998 fue otorgado a Amartya Sen ""por sus contribuciones a la economía del bienestar"".", strftime("1998"), 6, NULL, NULL),

(8, 5, "El Premio Nobel de la Paz 1993 fue otorgado conjuntamente a Nelson Mandela y Frederik Willem de Klerk ""por su trabajo para la terminación pacífica del régimen del apartheid y por sentar las bases de una nueva Sudáfrica democrática"".", strftime("1993"), 10, 11, NULL),

(9, 5, "Es la primera persona en el mundo occidental que nos ha demostrado que se puede librar una lucha sin violencia. Él es el primero en hacer el mensaje de el amor fraternal es una realidad en el curso de su lucha, y él ha llevado este mensaje a todos los hombres, a todas las naciones y razas. Hoy rendimos homenaje a Martin Luther King, el hombre que nunca ha abandonado su fe en la lucha desarmada que está librando, que ha sufrido por su fe, que ha sido encarcelado en muchas ocasiones, cuyo hogar ha sido objeto de ataques con bombas, cuyo la vida y las vidas de su familia han sido amenazadas y, sin embargo, nunca han fallado. Para este intrépido campeón de la paz, el Comité Nobel del Parlamento noruego ha otorgado el Premio de la Paz para el año 1964.", strftime("1964"), 12, NULL, NULL),

(10, 3, "El Premio Nobel de Fisiología o Medicina 1930 fue otorgado a Karl Landsteiner ""por su descubrimiento de grupos de sangre humana"".", strftime("1930"), 13, NULL, NULL),

(11, 2, "El Premio Nobel de Química de 1958 fue otorgado a Frederick Sanger ""por su trabajo sobre la estructura de las proteínas, especialmente la de la insulina"".", strftime("1958"), 14, NULL, NULL), 

(11, 2, "El Premio Nobel de Química de 1980 fue dividido, una mitad otorgado a Paul Berg ""por sus estudios fundamentales de la bioquímica de ácidos nucleicos, con especial atención al ADN recombinante"", la otra mitad conjuntamente a Walter Gilbert y Frederick Sanger ""por sus contribuciones sobre la determinación de secuencias de bases en ácidos nucleicos"".", strftime("1980"), 14, 15, 16),

(12, 2, "El Premio Nobel de Química de 1980 fue dividido, una mitad otorgado a Paul Berg ""por sus estudios fundamentales de la bioquímica de ácidos nucleicos, con especial atención al ADN recombinante"", la otra mitad conjuntamente a Walter Gilbert y Frederick Sanger ""por sus contribuciones sobre la determinación de secuencias de bases en ácidos nucleicos"".", strftime("1980"), 14, 15, 16),

(13, 1, "El Premio Nobel de Física 2017 fue dividido, una mitad otorgada a Rainer Weiss, la otra mitad conjuntamente a Barry C. Barish y Kip S. Thorne ""por contribuciones decisivas al detector LIGO y la observación de ondas gravitacionales"".", strftime("2017"), 17, 18, 19);


